import React, { useState } from "react";
import "../stylesheets/layouts/Favs.scss";
import { Link } from "react-router-dom";
import logo from "../images/logo-w.png";

const Favs = (props) => {
  const favDogsStr = localStorage.getItem("favDogs");
  const favDogs = (favDogsStr === null) ? [] : JSON.parse( favDogsStr );

  favDogs.push( props.dogs[ index ] );

  let genderColor = "";
  if (props.dogs.gender === "Hembra") {
    genderColor = "pink";
  } else {
    genderColor = "blue";
  }
  return (
    <>
      <header className="header">
        <div className="logo">
          <img className="header__logo" src={logo} alt="logo waggin" />
        </div>
        <div className="header__others">
          <Link className="link__search" to="/">
            <i class="header__dogFav--search fas fa-search"></i>
          </Link>
          <Link className="link__search" to="/Favs">
            <i class="header__dogFav--bone fas fa-bone"></i>
          </Link>
        </div>
      </header>

    <article className="article">
      <div className="article__containerImg"> <img
          className="article__image--background"
          src={favDogs.url}
          alt={favDogs.name}
        />
        <img
          className="article__image"
          src={favDogs.url}
          alt={favDogs.name}
        />
      </div>
      <div className="underPhoto">
        <div className="nameGender">
          <p className="nameGender__name"> {favDogs.name} </p>
          <div className="nameGender__gender">
            {favDogs.gender === "Hembra" ? (
              <i
                className={` nameGender__gender--female fas fa-venus  ${genderColor}`}
              ></i>
            ) : (
              <i
                className={` nameGender__gender--male fas fa-mars ${genderColor}`}
              ></i>
            )}
          </div>
        </div>
      </div>
      <Link className="link" to={"/dog/" + favDogs.id}>
        <li className="">
          <button className="article__button"> Ver perfil </button>
        </li>
      </Link>
    </article>
  );
    </>

  );
};

export default Favs;
